define(["jquery","semantic","utils","gitee"],(function(e,n,i,l){return{data:()=>({active:"bookmarks",level1Menus:[],level2Menus:[],level3Menus:[]}),watch:{$route(e,n){this.active=e.path.substr(1),"/login"===n.path&&0===this.level1Menus.length&&this.loadLevelMenu()}},methods:{route(e){e!==this.active&&(this.$router.push(e),this.active=e)},toggleLeftMenu(){e("#app-leftMenu").sidebar({closable:!1}).sidebar("toggle")},loadLevelMenu(){l.getFileContent("config/wyd2021.json",!1,!0).then((n=>{this.level1Menus=n.level1Menus,this.level2Menus=n.level2Menus,this.level3Menus=n.level3Menus,this.$nextTick((()=>{e(".dropdown").dropdown({on:"hover",action:function(n,i){e(this).dropdown("hide")}})}))}))},goURL(e){window.open(e)}},mounted(){this.loadLevelMenu()},template:'\n            <div class="ui menu fixed-top raised inverted wyd-header wyd-border-bottom" wydFlag="header">\n            \n              <div class="header item link" @click="toggleLeftMenu">\n                <img class="ui avatar image" src="/cdn/logo.jpg" />\n                <span class="font-weight-bold">Wyd2021</span>\n              </div>\n              \n              <a class="item hidden-xs-only" :class="active===lv1Menu[1]?\'active\':\'\'" v-for="lv1Menu in level1Menus" @click="route(lv1Menu[1])">\n                <i class="icon large" :class="lv1Menu[2]"></i>\n                <span>{{lv1Menu[0]}}</span>\n              </a>\n               \n              <div class="ui pointing dropdown link item hidden-xs-only" v-for="lv2Menu in level2Menus">\n                <i class="icon large" :class="lv2Menu.icon"></i>\n                <span class="text">{{lv2Menu.title}}</span>\n                <i class="dropdown icon"></i>\n                <div class="menu">\n                    <template v-for="lv2Link in lv2Menu.children">\n                        <div v-if="typeof lv2Link===\'string\'" class="header">{{lv2Link}}</div>\n                        <div v-else class="item" :class="active===lv2Link[1]?\'active\':\'\'" @click="route(lv2Link[1])">{{lv2Link[0]}}</div>\n                    </template>\n                </div>\n              </div>\n              \n              <div class="ui pointing dropdown link item hidden-xs-only" v-for="lv3Menu in level3Menus">\n                <i class="icon large" :class="lv3Menu.icon"></i>\n                <span class="text">{{lv3Menu.title}}</span>\n                <i class="dropdown icon"></i>\n                <div class="menu">\n                    <template v-for="lv3Link in lv3Menu.children">\n                        <div v-if="typeof lv3Link===\'string\'" class="header">{{lv3Link}}</div>\n                        <div v-else  class="item" @click="goURL(lv3Link[1])">{{lv3Link[0]}}</div>\n                    </template>\n                </div>\n              </div>\n              \n              \n            </div>\n        '}}));